import React from 'react';
import { Form,Input,Button,Avatar,Card } from 'antd';
import Icon from '@ant-design/icons';

const dummy = {
    isLoggedIn : true,
    imagePaths: [],
    mainPosts: [
        {
            User:{
                id:1,
                nickname:'livinoid98',
            },
            content:'첫 번째 게시글',
            img:'https://cdn.pixabay.com/photo/2018/08/14/13/23/ocean-3605547_960_720.jpg',
        },
    ],
}

const Home = () => {
    return(
        <div>
            {dummy.isLoggedIn && <Form encType="multipart/form-data" style={{margin:'20px'}}>
                <Input.TextArea maxLength={140} placeholder="어떤 신기한 일이 있었나요?"/>
                <div>
                    <input type="file" multiple hidden/>
                    <Button>이미지 업로드</Button>
                    <Button type="primary" style={{ float:'right' }} htmlType="submit">짹짹</Button>
                </div>
                <div>
                    {dummy.imagePaths.map((v,i) => {
                        <div key={v} style={{display:'inline-block'}}>
                            <img src={'http://localhost:3000/'+ v} alt="" style={{width:'200px'}}/>
                            <div>
                                <Button>제거</Button>
                            </div>
                        </div>
                    })}
                </div>
            </Form>}
            {dummy.mainPosts.map((c)=>{
                return(
                    <Card
                        key={+c.createdAt}
                        cover={c.img && <img src={c.img} alt="example"/>}
                        actions={[
                            <Icon type="retweet" key="retweet"/>,
                            <Icon type="heart" key="heart"/>,
                            <Icon type="message" key="message"/>,
                            <Icon type="ellipsis" key="ellipsis"/>
                        ]}
                        extra={<Button>팔로우</Button>}
                    >
                        <Card.Meta
                            avatar={<Avatar>{c.User.nickname[0]}</Avatar>}
                            title={c.User.nickname}
                            description={c.content}
                        />
                    </Card>
                );
            })}
        </div>
    );
};

export default Home;
