import {all, call, put, fork, takeLatest, takeEvery} from 'redux-saga/effects'
import {LOG_IN_REQUEST, LOG_IN_SUCCESS, LOG_IN_FAILURE, SIGN_UP_REQUEST, SIGN_UP_SUCCESS, SIGN_UP_FAILURE} from '../reducers/user'

function loginAPI(){
    // 서버에 요청을 보내는 부분
}

function signUpAPI(){

}

function* login(){
    try{
        yield call(loginAPI);
        yield put({ //put은 dispatch와 동일
            type:LOG_IN_SUCCESS,
        });
    }catch(e){ //loginAPI 실패
        console.error(e);
        yield put({
            type:LOG_IN_FAILURE,
        });
    }
}

function* signUp(){
    try{
        yield call(signUpAPI);
        yield put({
            type : SIGN_UP_SUCCESS,
        });
    }catch(e){
        console.error(e);
        yield put({
            type:SIGN_UP_FAILURE,
        });
    }
}

function* watchLogin(){
    yield takeLatest(LOG_IN_REQUEST, login);
}

function* watchSignUp(){
    yield takeEvery(SIGN_UP_REQUEST, signUp);
}

export default function* userSaga(){
    yield all([
        fork(watchLogin),
        fork(watchSignUp),
    ]);
}
